let currentIndex = 0;
const hello = [
  'optimaliserings',
  'datainnsamlings',
  'analyse',
  'integrasjons',
  'samarbeids',
  'rapporterings',
  'f√¥r-oversikts',
  'produksjonskost',
  'dataflyt',
  'arbeids',
];

let currentWord = '';
let isDeleting = false;
let letterIndex = 0;
const typingSpeed = 70; // Speed of typing
const deletingSpeed = 50; // Speed of deleting
const delayBetweenWords = 800; // Delay before starting to type the next word

function type() {
  const word = hello[currentIndex];

  if (!isDeleting) {
    // Typing the word
    currentWord = word.substring(0, letterIndex + 1);
    letterIndex++;
  } else {
    // Deleting the word
    currentWord = word.substring(0, letterIndex - 1);
    letterIndex--;
  }

  // Update the text content
  document.getElementById('changing-text').textContent = currentWord;

  // If the word is completely typed
  if (!isDeleting && letterIndex === word.length) {
    isDeleting = true;
    setTimeout(type, delayBetweenWords); // Pause before starting to delete
  }
  // If the word is completely deleted
  else if (isDeleting && letterIndex === 0) {
    isDeleting = false;
    currentIndex = (currentIndex + 1) % hello.length; // Move to the next word
    setTimeout(type, typingSpeed); // Start typing the next word
  } else {
    // Continue typing or deleting
    const speed = isDeleting ? deletingSpeed : typingSpeed;
    setTimeout(type, speed);
  }
}

// Start the typing effect once the DOM is fully loaded
window.onload = function () {
  type();
};
